insert into public.guided_days (week, day, title, description, type, estimated_minutes, audio_url) values (1, 1, 'Apagar apps inúteis e notificações', 'Delete aplicativos que sugam seu tempo. Desative notificações desnecessárias. Deixe apenas o essencial.', 'action', 15, '/audio/week1-intro.mp3');
insert into public.guided_days (week, day, title, description, type, estimated_minutes, audio_url) values (1, 2, 'Acordar 30min mais cedo', 'Configure o despertador 30 minutos antes do horário normal. Use esse tempo para si mesmo, sem pressa.', 'action', 30, '/audio/week1-intro.mp3');
insert into public.guided_days (week, day, title, description, type, estimated_minutes, audio_url) values (1, 3, 'Começar hábito de água + sol', 'Beba um copo d''água ao acordar e tome 5 minutos de sol matinal. Conecte-se com o básico.', 'action', 10, '/audio/week1-intro.mp3');
insert into public.guided_days (week, day, title, description, type, estimated_minutes, audio_url) values (1, 4, 'Leitura mínima (5min) + reflexão', 'Leia por 5 minutos algo que agrega valor. Depois reflita: o que aprendi? Como aplico?', 'reflection', 10, '/audio/week1-intro.mp3');
insert into public.guided_days (week, day, title, description, type, estimated_minutes, audio_url) values (1, 5, 'Diário de sabotagens', 'Anote tudo que te atrasa durante o dia: distrações, procrastinação, vícios. Seja honesto.', 'reflection', 10, '/audio/week1-intro.mp3');
insert into public.guided_days (week, day, title, description, type, estimated_minutes, audio_url) values (1, 6, 'Meditação 4min + frase de comando', '4 minutos de respiração consciente. Depois escolha uma frase motivadora e repita 3 vezes em voz alta.', 'action', 7, '/audio/week1-intro.mp3');
insert into public.guided_days (week, day, title, description, type, estimated_minutes, audio_url) values (1, 7, 'Relatório mental da semana', 'O que mudou? Como se sente? Quais resistências surgiram? Anote suas percepções.', 'reflection', 15, '/audio/week1-intro.mp3');
insert into public.guided_days (week, day, title, description, type, estimated_minutes, audio_url) values (2, 1, '7min de treino funcional', 'Flexões, agachamentos, prancha. 7 minutos de movimento intenso. Não precisa de academia.', 'action', 7, '/audio/week2-body.mp3');
insert into public.guided_days (week, day, title, description, type, estimated_minutes, audio_url) values (2, 2, 'Comer proteína real no café', 'Ovos, frango, shake proteico. Substitua pão e doces por proteína de verdade.', 'action', 15, '/audio/week2-body.mp3');
insert into public.guided_days (week, day, title, description, type, estimated_minutes, audio_url) values (2, 3, 'Banho gelado curto', '30 segundos de água fria no final do banho. Respire fundo e aguente o desconforto.', 'challenge', 1, '/audio/week2-body.mp3');
insert into public.guided_days (week, day, title, description, type, estimated_minutes, audio_url) values (2, 4, 'Lembrete de postura (4x no dia)', 'Configure 4 alarmes. A cada toque, corrija sua postura: ombros para trás, peito aberto.', 'action', 5, '/audio/week2-body.mp3');
insert into public.guided_days (week, day, title, description, type, estimated_minutes, audio_url) values (2, 5, 'Anotar o que comeu (sem julgamento)', 'Liste tudo que consumiu hoje. Não julgue, apenas observe padrões e tome consciência.', 'reflection', 10, '/audio/week2-body.mp3');
insert into public.guided_days (week, day, title, description, type, estimated_minutes, audio_url) values (2, 6, 'Treino + respiração tática', '7 minutos de exercício seguidos de 3 minutos de respiração 4-4-4-4 para recuperação.', 'action', 10, '/audio/week2-body.mp3');
insert into public.guided_days (week, day, title, description, type, estimated_minutes, audio_url) values (2, 7, 'Review físico (como se sentiu?)', 'Como o corpo reagiu esta semana? Mais energia? Menos? Quais mudanças nota?', 'reflection', 10, '/audio/week2-body.mp3');
insert into public.guided_days (week, day, title, description, type, estimated_minutes, audio_url) values (3, 1, 'Visualização: ''meu eu daqui 90 dias''', 'Feche os olhos e veja-se daqui 3 meses: mais forte, disciplinado, focado. Sinta essa versão de si.', 'reflection', 10, '/audio/week3-mind.mp3');
insert into public.guided_days (week, day, title, description, type, estimated_minutes, audio_url) values (3, 2, 'Escrita de metas reais', '3 objetivos específicos para os próximos 30 dias. Seja preciso: datas, números, resultados.', 'action', 15, '/audio/week3-mind.mp3');
insert into public.guided_days (week, day, title, description, type, estimated_minutes, audio_url) values (3, 3, 'Meditação 10min com técnica guiada', 'Use a técnica Box Breathing por 10 minutos completos. Mantenha foco total na respiração.', 'action', 10, '/audio/week3-mind.mp3');
insert into public.guided_days (week, day, title, description, type, estimated_minutes, audio_url) values (3, 4, 'Silêncio total 20min no dia', '20 minutos sem falar, sem tela, sem música. Apenas você e seus pensamentos. Observe-os.', 'challenge', 20, '/audio/week3-mind.mp3');
insert into public.guided_days (week, day, title, description, type, estimated_minutes, audio_url) values (3, 5, 'Sem redes sociais por 24h', 'Delete apps temporariamente ou dê o celular para alguém. 24h de detox digital completo.', 'challenge', 24, '/audio/week3-mind.mp3');
insert into public.guided_days (week, day, title, description, type, estimated_minutes, audio_url) values (3, 6, 'Leitura profunda (capítulo inteiro)', 'Leia um capítulo completo sem interrupções. Faça anotações. Absorva o conteúdo totalmente.', 'action', 30, '/audio/week3-mind.mp3');
insert into public.guided_days (week, day, title, description, type, estimated_minutes, audio_url) values (3, 7, 'Carta para si mesmo', 'Escreva uma carta para você daqui 1 ano. O que quer ter conquistado? Como quer se sentir?', 'reflection', 20, '/audio/week3-mind.mp3');
insert into public.guided_days (week, day, title, description, type, estimated_minutes, audio_url) values (4, 1, 'Análise do ciclo (o que mudou?)', 'Compare você de 3 semanas atrás com agora. Liste mudanças físicas, mentais e comportamentais.', 'reflection', 20, '/audio/week4-identity.mp3');
insert into public.guided_days (week, day, title, description, type, estimated_minutes, audio_url) values (4, 2, 'Reescrever rotina ideal com base no teste', 'Crie sua rotina perfeita baseada no que funcionou nas 3 semanas. Seja específico com horários.', 'action', 25, '/audio/week4-identity.mp3');
insert into public.guided_days (week, day, title, description, type, estimated_minutes, audio_url) values (4, 3, 'Meditação modo guerreiro + frase nova', '10 minutos visualizando sua versão alpha. Crie uma nova frase de poder e a repita 5 vezes.', 'action', 15, '/audio/week4-identity.mp3');
insert into public.guided_days (week, day, title, description, type, estimated_minutes, audio_url) values (4, 4, 'Rotina limpa (executar tudo sem falha)', 'Execute sua rotina ideal sem pular nada. Manhã, tarde e noite perfeitas. Zero exceções.', 'challenge', null, '/audio/week4-identity.mp3');
insert into public.guided_days (week, day, title, description, type, estimated_minutes, audio_url) values (4, 5, 'Desafio físico + mental', 'Dobrar o treino (14min) + zero distrações digitais por 4 horas seguidas. Teste seus limites.', 'challenge', 4, '/audio/week4-identity.mp3');
insert into public.guided_days (week, day, title, description, type, estimated_minutes, audio_url) values (4, 6, 'Planejar os próximos 30 dias', 'Defina objetivos, rotinas e métricas para o próximo mês. Como vai manter o momentum?', 'action', 30, '/audio/week4-identity.mp3');
insert into public.guided_days (week, day, title, description, type, estimated_minutes, audio_url) values (4, 7, 'Ritual de encerramento (escrever missão)', 'Escreva sua missão pessoal em uma frase. Quem você é? Qual seu propósito? Memorize-a.', 'reflection', 25, '/audio/week4-identity.mp3');
